mapper.select:
SELECT * FROM CAR JOIN RENTCAR ON CAR.RENT_CODE = RENTCAR.RENT_CODE JOIN CAR_IMG ON CAR.CAR_NO = CAR_IMG.CAR_NO WHERE LOWER(CAR.CAR_NAME) LIKE '%' || LOWER(#{carName}) || '%'
AND CAR.CAR_TYPE = #{carType}
AND NOT EXISTS ( SELECT 1 FROM RES_CAR WHERE CAR.CAR_NO = RES_CAR.CAR_NO AND ( (RES_CAR.RENT_START <= #{outDate} AND RES_CAR.RENT_END >= #{inDate}) OR (RES_CAR.RENT_START <= #{outDate} AND RES_CAR.RENT_END >= #{outDate}) OR (RES_CAR.RENT_START >= #{inDate} AND RES_CAR.RENT_END <= #{outDate}) ) )

	SELECT * FROM RES_CAR RC, CARREVIEW CV
     WHERE RC.RES_C_NO = CV.RES_C_NO
       AND TRIM(CAR_NO) = #{car_no}

select * from res_car
where user_id = #{userId}
and CNP_CODE = #{code}
